sys_script_include.script:
var Event = Class.create();
Event.prototype = {
	initialize: function() {
	},

	getEventObj : function(eventID) {

		var obj = {
			"id" : "",
			"name" : "",
			"active" : false,
			"silent" : false,
			"date" : ""
		};

		var eventGr = new GlideRecord(Constants.EVENT_TABLE);
		if (!eventGr.get(eventID)) {
			return obj;
		}

		obj.id     = eventGr.getUniqueValue();
		obj.name   = eventGr.getValue('name');
		obj.active = eventGr.getValue('active') == "1";
		obj.silent = eventGr.getValue('silent') == "1";
		obj.date   = eventGr.date.getDisplayValue();

		return obj;

	},

	type: 'Event'
};
